// Login.js
import React, { useState } from 'react'
import '../styles/login.css'
import Signup from '../pages/signup'

const LoginWindow = () => {
    const [showSignup, setShowSignup] = useState(false);

    const toggleSignup = () => {
        setShowSignup(!showSignup);
    };

    return (
        <div className="login-overlay">
            <div className="login-popup">
                <div className="boxLogin">
                    <div>
                        <a className="loginlogoPicsLgn" href="/"> <img src={process.env.PUBLIC_URL + '/images/investlylogo.jpg'} alt="" /></a>
                        <a className="loginlogoNameLgn" href="/">Investly</a>
                    </div>

                    <div className="details_boxLgn"><input id="userid" className="borderLgn" type="email" pattern=".+@globex\.com"
                            placeholder="Email id" />
                    </div>
                    <div className="details_boxLgn"><input id="password" className="borderLgn" type="password"
                            placeholder="Password" />
                    </div>

                    <div className="loginBtnBox">
                        <a className="logBtn" href="#" id="login">Login</a>
                        <a className="CancelBtnLgn" href="#" id="cancel">Cancel</a>
                    </div>

                    <div className="NoaccountLgn">
                        <a onClick={toggleSignup}>Don't have an account? Sign up now</a>
                    </div>
                </div>
            </div>
            {showSignup && <Signup onClose={toggleSignup} />}
        </div>
    );
}

export default LoginWindow


